import "./styles.scss";
import React, { useEffect, useState, useRef } from "react";
import ReactDOM from "react-dom";
import { createEditor } from "../editor";
import debounce from "debounce";
import Link from "next/link";
import Router from "next/router";

// default code
const code = `import x from 'x';
// edit this example
function Greet() {
  return <span>Hello World!</span>
}
<Greet />
`;
export default function SandBox() {
  const [codeInput, setCodeInput] = useState(code);
  let editor = null;
  const el = useRef(null);
  const runCode = () => {
    editor = createEditor(el.current);
    editor.run(codeInput);
    run(codeInput);
  };
  const onCodeChange = ({ target: { value } }) => {
    setCodeInput(value);
  };
  const run = () => {
    editor.run(codeInput);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(e);

    fetch("/api/codetest", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        candidate_email: "9999@gmail.comzowie",
        codes: "9d999",
        city: "8888dd8",
      }),
    }).then((res) => {
      // Do a fast client-side transition to the already prefetched dashboard page
      if (res.ok) Router.push("/api/codetest");
    });
  };

  return (
    <div className="app">
      <form onSubmit={(e) => handleSubmit(e)}>
        <label>Email:</label>
        <input name="candidate_email" type="text" />
        <label>City:</label>
        <input name="city" type="text" />

        <div className="split-view">
          <div className="code-editor">
            <textarea value={codeInput} onChange={onCodeChange} />
          </div>
          <div className="preview" ref={el} />
        </div>

        <input type="submit" value="Submit" />
      </form>
      <button onClick={runCode}>Run</button>
      <Link href="/">
        <button>Go Back To Home</button>
      </Link>
    </div>
  );
}
